ESX                           = nil

Citizen.CreateThread(function()
	while ESX == nil do
		TriggerEvent('esx:getSharedObject', function(obj) ESX = obj end)
		Citizen.Wait(0)
	end

	while ESX.GetPlayerData().job == nil do
		Citizen.Wait(10)
	end

	PlayerData = ESX.GetPlayerData()
end)

function SetVehicleMaxMods(vehicle)
	local props = {
		modEngine       = 2,
		modBrakes       = 2,
		modTransmission = 2,
		modSuspension   = 3,
		modTurbo        = true
	}

	ESX.Game.SetVehicleProperties(vehicle, props)
end

function cleanPlayer(playerPed)
	SetPedArmour(playerPed, 0)
	ClearPedBloodDamage(playerPed)
	ResetPedVisibleDamage(playerPed)
	ClearPedLastWeaponDamage(playerPed)
	ResetPedMovementClipset(playerPed, 0)
end

function setUniform(job, playerPed)
	TriggerEvent('skinchanger:getSkin', function(skin)
		if skin.sex == 0 then
			if Config.Uniforms[job].male ~= nil then
				TriggerEvent('skinchanger:loadClothes', skin, Config.Uniforms[job].male)
			else
				ESX.ShowNotification(_U('no_outfit'))
			end

			if job == 'bullet_wear' then
				SetPedArmour(playerPed, 100)
			end
		else
			if Config.Uniforms[job].female ~= nil then
				TriggerEvent('skinchanger:loadClothes', skin, Config.Uniforms[job].female)
			else
				ESX.ShowNotification(_U('no_outfit'))
			end

			if job == 'bullet_wear' then
				SetPedArmour(playerPed, 100)
			end
		end
	end)
end

function OpenCloakroomMenu()

	local playerPed = PlayerPedId()
	local grade = PlayerData.job.grade_name

	local elements = {
		{ label = '~r~Tenue Civil', value = 'citizen_wear' },
		}
		table.insert(elements, {label = '~g~Tenue Inspecteur 1', value = 'special_wear'})
		table.insert(elements, {label = '~g~Tenue Inspecteur 2', value = 'executif_wear'})
		table.insert(elements, {label = '-------------------------------', value = ''})
		table.insert(elements, {label = '~g~Mettre Sac', value = 'sac_wear'})
		table.insert(elements, {label = '~r~Enlever Sac', value = 'sac1_wear'})
		table.insert(elements, {label = '-------------------------------', value = ''})
		table.insert(elements, {label = '~g~Mettre Casque', value = 'casque_wear'})
		table.insert(elements, {label = '~r~Enlever Casque', value = 'casque1_wear'})
		table.insert(elements, {label = '-------------------------------', value = ''})
		table.insert(elements, {label = '~g~Mettre Cagoule', value = 'cagoule_wear'})
		table.insert(elements, {label = '~r~Enlever Cagoule', value = 'cagoule1_wear'})
		table.insert(elements, {label = '-------------------------------', value = ''})
		table.insert(elements, {label = '~g~Mettre Lunette operation', value = 'luno_wear'})
		table.insert(elements, {label = '~r~Enlever Lunette operation', value = 'luno1_wear'})

	ESX.UI.Menu.CloseAll()

	ESX.UI.Menu.Open('default', GetCurrentResourceName(), 'cloakroom',
	{
		title    = _U('cloakroom'),
		align    = 'top-left',
		elements = elements
	}, function(data, menu)

		cleanPlayer(playerPed)

		if data.current.value == 'citizen_wear' then

				ESX.TriggerServerCallback('esx_skin:getPlayerSkin', function(skin)
					TriggerEvent('skinchanger:loadSkin', skin)
				end)

			if Config.MaxInService ~= -1 then

				ESX.TriggerServerCallback('esx_service:isInService', function(isInService)
					if isInService then

						playerInService = false

						local notification = {
							title    = _U('service_anonunce'),
							subject  = '',
							msg      = _U('service_out_announce', GetPlayerName(PlayerId())),
							iconType = 1
						}

						TriggerServerEvent('esx_service:notifyAllInService', notification, 'inspecteur')

						TriggerServerEvent('esx_service:disableService', 'inspecteur')
						--TriggerEvent('jek_inspecteurjob:updateBlip')
						ESX.ShowNotification(_U('service_out'))
					end
				end, 'inspecteur')
			end

		end

		if Config.MaxInService ~= -1 and data.current.value ~= 'citizen_wear' then
			local serviceOk = 'waiting'

			ESX.TriggerServerCallback('esx_service:isInService', function(isInService)
				if not isInService then

					ESX.TriggerServerCallback('esx_service:enableService', function(canTakeService, maxInService, inServiceCount)
						if not canTakeService then
							ESX.ShowNotification(_U('service_max', inServiceCount, maxInService))
						else

							serviceOk = true
							playerInService = true

							local notification = {
								title    = _U('service_anonunce'),
								subject  = '',
								msg      = _U('service_in_announce', GetPlayerName(PlayerId())),
								iconType = 1
							}
	
							TriggerServerEvent('esx_service:notifyAllInService', notification, 'inspecteur')
							--TriggerEvent('jek_inspecteurjob:updateBlip')
							ESX.ShowNotification(_U('service_in'))
						end
					end, 'inspecteur')

				else
					serviceOk = true
				end
			end, 'inspecteur')

			while type(serviceOk) == 'string' do
				Citizen.Wait(5)
			end

			-- if we couldn't enter service don't let the player get changed
			if not serviceOk then
				return
			end
		end

		if
			data.current.value == 'special_wear' or
			data.current.value == 'executif_wear' or
			data.current.value == 'boss_wear' or
			data.current.value == 'bullet_wear' or
			data.current.value == 'bullet1_wear' or
			data.current.value == 'sac_wear' or
			data.current.value == 'sac1_wear' or			
			data.current.value == 'casque_wear' or
			data.current.value == 'casque1_wear' or
			data.current.value == 'cagoule_wear' or
			data.current.value == 'cagoule1_wear' or
			data.current.value == 'luno_wear' or
			data.current.value == 'luno1_wear' 

		then
			setUniform(data.current.value, playerPed)
		end

		if data.current.value == 'freemode_ped' then
			local modelHash = ''

			ESX.TriggerServerCallback('esx_skin:getPlayerSkin', function(skin, jobSkin)
				if skin.sex == 0 then
					modelHash = GetHashKey(data.current.maleModel)
				else
					modelHash = GetHashKey(data.current.femaleModel)
				end

				ESX.Streaming.RequestModel(modelHash, function()
					SetPlayerModel(PlayerId(), modelHash)
					SetModelAsNoLongerNeeded(modelHash)

					TriggerEvent('esx:restoreLoadout')
				end)
			end)

		end



	end, function(data, menu)
		menu.close()

		CurrentAction     = 'menu_cloakroom'
		CurrentActionMsg  = _U('open_cloackroom')
		CurrentActionData = {}
	end)
end

